function _container1(html){return(
html`
  <div class="india-with-disputed">
    <svg></svg>
  </div>
`
)}

async function _vis1(d3,html)
{
  d3.select('.india-with-disputed > svg').remove()
  d3.select('.india-with-disputed .tooltip').remove()
  const width = 500;
  const height = 500;
  const d3SelectionOfSVGCanvas = d3.select('.india-with-disputed')
    .append('svg')
    .attr('class', 'my-canvas')
    .attr('width', width)
    .attr('height', height)
    .style('background-color', '#ffff');
  const g = d3SelectionOfSVGCanvas.append("g");
  const url = "https://raw.githubusercontent.com/HindustanTimesLabs/shapefiles/master/india/state_ut/india_state.json";
  const json = await d3.json(url);
  const myData = {
    "NCT of Delhi": 150,
    "Haryana": 120,
    "Punjab": 200,
    "Uttar Pradesh": 120,
    "Rajasthan": 80,
  };
  const colorScale = d3.scaleSequential()
    .domain([0, d3.max(Object.values(myData))])
    .interpolator(d3.interpolateBlues);
  const projection = d3.geoMercator().fitSize([width, height], json);
  const path = d3.geoPath().projection(projection);
  const tooltip = d3.select('.india-with-disputed')
    .append('div')
    .attr('class', 'tooltip')
    .style('position', 'absolute')
    .style('padding', '8px 12px')
    .style('background', 'rgba(0, 0, 0, 0.85)')
    .style('color', 'white')
    .style('border-radius', '4px')
    .style('font-size', '13px')
    .style('pointer-events', 'none')
    .style('z-index', '1000')
    .style('opacity', 0)
    .style('transition', 'opacity 0.2s');
  d3SelectionOfSVGCanvas.selectAll("path")
    .data(json.features)
    .enter()
    .append('path')
    .attr('d', path)
    .attr('fill', d => {
      const stateName = d.properties.ST_NM;
      return myData[stateName] ? colorScale(myData[stateName]) : '#e0e0e0';
    })
    .attr('stroke', '#fff')
    .attr('stroke-width', 0.5)
    .style('cursor', 'pointer')
    .on('mouseover', function(event, d) {
      const stateName = d.properties.ST_NM;
      const value = myData[stateName];
      d3.select(this)
        .attr('stroke', '#333')
        .attr('stroke-width', 1)
        .attr('opacity', 0.8);
      tooltip
        .style('opacity', 1)
        .html(`<strong>${stateName}</strong><br/>Projects Completed: ${value || 'No data'}`)
        .style('left', (event.pageX + 15) + 'px')
        .style('top', (event.pageY - 28) + 'px');
    })
    .on('mousemove', event => {
      tooltip
        .style('left', (event.pageX + 15) + 'px')
        .style('top', (event.pageY - 28) + 'px');
    })
    .on('mouseout', function() {
      d3.select(this)
        .attr('stroke', '#fff')
        .attr('stroke-width', 0.5)
        .attr('opacity', 1);
      tooltip.style('opacity', 0);
    });
  
  return html`<div class="india-with-disputed"></div>`
}


export default function define(runtime, observer) {
  const main = runtime.module();
  main.variable(observer("container1")).define("container1", ["html"], _container1);
  main.variable(observer("vis1")).define("vis1", ["d3","html"], _vis1);
  return main;
}
